import React, { useState, useEffect, useRef } from 'react';
import './HouseOfCompaniesChatbot.css';

const HouseOfCompaniesChatbot = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [messages, setMessages] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState({});
  const [totalPrice, setTotalPrice] = useState(0);
  const [userProfile, setUserProfile] = useState({
    name: '', email: '', phone: '', linkedin: '', company: '', country: ''
  });
  const [showFreeTextInput, setShowFreeTextInput] = useState(false);
  const [freeTextInput, setFreeTextInput] = useState('');
  const messagesEndRef = useRef(null);

  const sessionId = useRef('HOC-' + Date.now() + '-' + Math.random().toString(36).substr(2, 9)).current;
  const totalSteps = 12;

  // Simulated chatFlow (abbreviated for brevity)
  const chatFlow = {
    0: {
      message: "👋 Welcome to House of Companies! What brings you here today?",
      options: [
        { id:'setup', title:'🏢 Set up a new business', subtitle:'Start in Europe or expand globally' },
        { id:'expand', title:'🌍 Expand existing business', subtitle:'Grow to new markets' },
        { id:'consultation', title:'💬 Get expert consultation', subtitle:'Speak with specialists' }
      ]
    },
    // ... other steps ...
  };

  // Auto-scroll
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  // Initialize
  useEffect(() => {
    if (messages.length === 0) addBotMessage(chatFlow[0].message);
  }, []);

  const addBotMessage = text => {
    setMessages(m => [...m, { sender:'bot', text, time:new Date().toLocaleTimeString() }]);
  };

  const addUserMessage = text => {
    setMessages(m => [...m, { sender:'user', text, time:new Date().toLocaleTimeString() }]);
  };

  const handleOption = opt => {
    addUserMessage(opt.title);
    // ... your pricing & state logic ...
    setTimeout(() => addBotMessage("You picked “" + opt.title + "”"), 500);
  };

  const sendFreeText = () => {
    if (!freeTextInput.trim()) return;
    addUserMessage(freeTextInput);
    setFreeTextInput('');
    setShowFreeTextInput(false);
    setTimeout(() => addBotMessage("Thanks for your question! We'll get back to you."), 800);
  };

  return (
    <div className="chatbot-container">
      {/* Header */}
      <div className="chat-header">
        <h1>House of Companies</h1>
        <div className="subtitle">Business Formation Assistant</div>
        <div className="session-info">Session: {sessionId.split('-')[1]}</div>
        <div className="session-info">{new Date().toLocaleDateString()}</div>
        <div className="progress-bar">
          <div
            className="progress-fill"
            style={{ width: `${Math.round((messages.length/totalSteps)*100)}%` }}
          />
        </div>
        <div style={{ marginTop:5, fontSize:12 }}>Progress {Math.round((messages.length/totalSteps)*100)}%</div>
      </div>

      {/* Messages */}
      <div className="chat-messages" id="chatMessages">
        {messages.map((msg, i) => (
          <div key={i} className={`message ${msg.sender}`}>
            <div className="message-icon">
              {msg.sender==='bot' ? '🤖' : '👤'}
            </div>
            <div>
              <div className="message-time">{msg.time}</div>
              <div className="message-content">{msg.text}</div>
            </div>
          </div>
        ))}
        {/* Options for current step */}
        {chatFlow[currentStep]?.options && (
          <div className="options-container">
            {chatFlow[currentStep].options.map(opt => (
              <button
                key={opt.id}
                className="option-button"
                onClick={() => handleOption(opt)}
              >
                <div className="option-title">{opt.title}</div>
                <div className="option-subtitle">{opt.subtitle}</div>
              </button>
            ))}
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>

      {/* Input & Controls */}
      <div className="chat-input-container">
        <div className="chat-controls">
          <button className="control-button" onClick={() => {/* go back logic */}}>← Back</button>
          <button className="control-button active" onClick={() => window.location.reload()}>Start Over</button>
          <button className="control-button" onClick={() => setShowFreeTextInput(true)}>
            Ask a Question
          </button>
        </div>

        <div className="chat-input">
          {showFreeTextInput && (
            <>
              <input
                type="text"
                placeholder="Ask your question here..."
                value={freeTextInput}
                onChange={e => setFreeTextInput(e.target.value)}
                onKeyPress={e => e.key==='Enter' && sendFreeText()}
              />
              <button className="send-button" onClick={sendFreeText}>
                ➤
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default HouseOfCompaniesChatbot;
