import React, { useState, useEffect, useRef } from 'react';
import './HouseOfCompaniesChatbot.css';

const HouseOfCompaniesChatbot = () => {
  // ... your state and all logic remains the same ...

  // (all your code up to here stays unchanged)

  // --- ADD THIS FUNCTION ---
  const renderSelectionsSummary = () => (
    <div className="selections-summary-panel bg-blue-50 px-4 py-3 border-b border-blue-100">
      <div className="font-semibold text-blue-800 mb-1">Your Selections</div>
      <ul className="text-sm pl-3 list-disc">
        {Object.values(selectedOptions).length === 0 && (
          <li className="text-gray-400">No options selected yet.</li>
        )}
        {Object.values(selectedOptions).map((option, idx) => (
          <li key={idx}>
            {option.title} 
            {option.price > 0 && <span className="ml-2 text-blue-600">‚Ç¨{option.price}</span>}
          </li>
        ))}
        {userProfile && userProfile.country && (
          <li><b>Country:</b> {userProfile.country}</li>
        )}
      </ul>
    </div>
  );

  // --- END OF ADDED FUNCTION ---

  // ... keep your renderCurrentStep() here as before ...

  return (
    <div className="chatbot-container max-w-md mx-auto bg-white shadow-2xl rounded-lg overflow-hidden">
      {/* Header */}
      <div className="bg-gradient-to-r from-green-600 to-green-700 text-white p-4">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-lg font-bold">House of Companies</h1>
            <p className="text-sm opacity-90">Business Formation Assistant</p>
          </div>
          <div className="text-right text-xs">
            <div>Session: {sessionId.split('-')[1]}</div>
            <div>{new Date().toLocaleDateString()}</div>
          </div>
        </div>
      </div>

      {/* Progress Bar */}
      <div className="px-4 py-2 bg-gray-50 border-b">
        <div className="w-full bg-gray-200 rounded-full h-2">
          <div 
            className="bg-gradient-to-r from-green-500 to-green-600 h-2 rounded-full transition-all duration-500"
            style={{ width: `${Math.min((currentStep / totalSteps) * 100, 100)}%` }}
          ></div>
        </div>
        <div className="flex justify-between text-xs text-gray-600 mt-1">
          <span>Progress</span>
          <span>{Math.min(Math.round((currentStep / totalSteps) * 100), 100)}%</span>
        </div>
      </div>

      {/* Selections Summary Panel (ALWAYS VISIBLE!) */}
      {renderSelectionsSummary()}

      {/* Chat Messages */}
      <div className="chat-messages flex-1 overflow-y-auto p-4 space-y-4 h-96">
        {messages.map((message, index) => (
          <div key={index} className={`flex ${message.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
            <div className={`max-w-xs lg:max-w-md px-4 py-2 rounded-lg ${
              message.sender === 'user' 
                ? 'bg-green-500 text-white' 
                : 'bg-white text-gray-800 border border-gray-200'
            }`}>
              <div className="text-sm">{message.text}</div>
              <div className="text-xs opacity-70 mt-1">{message.timestamp}</div>
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Current Step Content */}
      <div className="p-4 border-t border-gray-200">
        {renderCurrentStep()}
      </div>

      {/* Free Text Input */}
      {showFreeTextInput && (
        <div className="p-4 border-t border-gray-200 bg-gray-50">
          <div className="flex space-x-2">
            <input
              type="text"
              value={freeTextInput}
              onChange={(e) => setFreeTextInput(e.target.value)}
              placeholder="Type your question..."
              className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
              onKeyPress={(e) => {
                if (e.key === 'Enter' && freeTextInput.trim()) {
                  handleFreeTextInput(freeTextInput.trim());
                }
              }}
            />
            <button
              onClick={() => freeTextInput.trim() && handleFreeTextInput(freeTextInput.trim())}
              className="px-4 py-2 bg-green-500 text-white rounded-md hover:bg-green-600 transition-colors"
            >
              Send
            </button>
          </div>
        </div>
      )}

      {/* Navigation & Action Buttons */}
      <div className="p-4 border-t border-gray-200 bg-gray-50">
        <div className="flex justify-between items-center mb-3">
          {currentStep > 0 && (
            <button
              onClick={goBack}
              className="px-4 py-2 bg-gray-500 text-white rounded-lg hover:bg-gray-600 transition-colors"
            >
              ‚Üê Back
            </button>
          )}
          <button
            onClick={startOver}
            className="px-4 py-2 bg-gradient-to-r from-green-500 to-green-600 text-white rounded-lg hover:from-green-600 hover:to-green-700 transition-all duration-300"
          >
            Start Over
          </button>
        </div>

        {/* Current Total Display */}
        {totalPrice > 0 && (
          <div className="bg-gradient-to-r from-green-500 to-green-600 text-white p-3 rounded-lg">
            <div className="text-center">
              <span className="text-sm font-medium">Current Total: </span>
              <span className="text-xl font-bold">‚Ç¨{totalPrice.toLocaleString()}</span>
            </div>
          </div>
        )}

        {/* Question Button */}
        <div className="mt-3">
          <button
            onClick={() => setShowFreeTextInput(!showFreeTextInput)}
            className="w-full px-4 py-2 bg-gray-200 text-gray-700 rounded-lg hover:bg-gray-300 transition-colors"
          >
            üí¨ Ask a Question
          </button>
        </div>
      </div>
    </div>
  );
};

export default HouseOfCompaniesChatbot;
