import React, { useState, useEffect, useRef } from 'react';
import './HouseOfCompaniesChatbot.css';

const HouseOfCompaniesChatbot = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [messages, setMessages] = useState([]);
  const [selectedOptions, setSelectedOptions] = useState({});
  const [totalPrice, setTotalPrice] = useState(0);
  const [userProfile, setUserProfile] = useState({
    name: '',
    email: '',
    phone: '',
    linkedin: '',
    company: '',
    country: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [sessionId] = useState(() => 'HOC-' + Date.now() + '-' + Math.random().toString(36).substr(2, 9));
  const [showFreeTextInput, setShowFreeTextInput] = useState(false);
  const [freeTextInput, setFreeTextInput] = useState('');
  const [errors, setErrors] = useState({});
  const messagesEndRef = useRef(null);
  const chatMessagesRef = useRef(null);

  const totalSteps = 12;

  // Countries list
  const countries = [
    'United States', 'United Kingdom', 'Germany', 'Netherlands', 'Ireland', 'Luxembourg',
    'Switzerland', 'France', 'Spain', 'Italy', 'Portugal', 'Belgium', 'Austria',
    'Denmark', 'Sweden', 'Norway', 'Finland', 'Poland', 'Czech Republic', 'Hungary',
    'Estonia', 'Latvia', 'Lithuania', 'Slovenia', 'Croatia', 'Romania', 'Bulgaria',
    'Cyprus', 'Malta', 'Canada', 'Australia', 'New Zealand', 'Singapore', 'Hong Kong',
    'UAE', 'Qatar', 'Saudi Arabia', 'India', 'Japan', 'South Korea', 'Malaysia',
    'Thailand', 'Philippines', 'Indonesia', 'Vietnam', 'South Africa', 'Mexico',
    'Brazil', 'Argentina', 'Chile', 'Colombia', 'Other'
  ];

  // Chat flow steps with services and pricing
  const chatFlow = {
    0: {
      message: "ðŸ‘‹ Welcome to House of Companies! I'm here to help you establish and grow your business across Europe and beyond. What brings you here today?",
      options: [
        { id: 'setup', title: 'ðŸ¢ Set up a new business', description: 'Start your company in Europe or expand globally' },
        { id: 'expand', title: 'ðŸŒ Expand existing business', description: 'Grow your business to new markets' },
        { id: 'consultation', title: 'ðŸ’¬ Get expert consultation', description: 'Speak with our business formation specialists' },
        { id: 'services', title: 'ðŸ“‹ Explore our services', description: 'Learn about our comprehensive business solutions' }
      ]
    },
    1: {
      message: "Great choice! Let me understand your business stage better. Where are you in your entrepreneurial journey?",
      options: [
        { id: 'idea', title: 'ðŸ’¡ I have a business idea', description: 'Just starting out with a concept' },
        { id: 'planning', title: 'ðŸ“‹ Planning phase', description: 'Working on business plan and strategy' },
        { id: 'ready', title: 'ðŸš€ Ready to launch', description: 'Prepared to establish the company' },
        { id: 'existing', title: 'ðŸª Existing business', description: 'Already running a business elsewhere' }
      ]
    },
    2: {
      message: "Perfect! Which regions are you most interested in for your business operations?",
      options: [
        { id: 'eu', title: 'ðŸ‡ªðŸ‡º European Union', description: 'Access to 27 EU countries and 450M+ consumers' },
        { id: 'uk', title: 'ðŸ‡¬ðŸ‡§ United Kingdom', description: 'English-speaking market with global reach' },
        { id: 'us', title: 'ðŸ‡ºðŸ‡¸ United States', description: 'World\'s largest economy and consumer market' },
        { id: 'global', title: 'ðŸŒ Multiple regions', description: 'International expansion across regions' }
      ]
    },
    3: {
      message: "Excellent! Now let's look at our core business formation services. Please select what you need:",
      options: [
        { id: 'virtual_office', title: 'ðŸ“ Virtual Office Address', description: 'Professional business address', price: 495 },
        { id: 'entity_setup', title: 'ðŸ¢ Entity Setup (LLC/Ltd)', description: 'Complete company formation', price: 1995 },
        { id: 'vat_id', title: 'ðŸ†” VAT ID Registration', description: 'EU VAT identification number', price: 325 },
        { id: 'bank_account', title: 'ðŸ¦ Business Bank Account', description: 'Multi-currency business banking', price: 750 },
        { id: 'skip_services', title: 'â­ï¸ Skip for now', description: 'Continue without selecting services', price: 0 }
      ]
    },
    4: {
      message: "Great! Let's look at our tax and compliance services:",
      options: [
        { id: 'tax_advisory', title: 'ðŸ“Š Tax Advisory Services', description: 'Expert tax planning and compliance', price: 850 },
        { id: 'bookkeeping', title: 'ðŸ“š Bookkeeping Services', description: 'Monthly financial record management', price: 450 },
        { id: 'annual_filing', title: 'ðŸ“‹ Annual Filing', description: 'Company annual returns and filings', price: 275 },
        { id: 'payroll', title: 'ðŸ’° Payroll Services', description: 'Employee payroll management', price: 650 },
        { id: 'skip_tax', title: 'â­ï¸ Skip tax services', description: 'Continue without tax services', price: 0 }
      ]
    },
    5: {
      message: "Perfect! Now let's explore our additional services and add-ons:",
      options: [
        { id: 'trademark', title: 'â„¢ï¸ Trademark Registration', description: 'Protect your brand internationally', price: 1250 },
        { id: 'website', title: 'ðŸŒ Professional Website', description: 'Business website development', price: 2500 },
        { id: 'marketing', title: 'ðŸ“ˆ Digital Marketing Setup', description: 'SEO and online presence', price: 1750 },
        { id: 'legal_docs', title: 'ðŸ“„ Legal Document Templates', description: 'Contracts and legal templates', price: 350 },
        { id: 'skip_addons', title: 'â­ï¸ Skip add-ons', description: 'Continue without additional services', price: 0 }
      ]
    },
    6: {
      message: "Excellent selections! When would you like to get started with your business setup?",
      options: [
        { id: 'asap', title: 'ðŸš€ As soon as possible', description: 'Start immediately' },
        { id: 'month', title: 'ðŸ“… Within a month', description: 'Planning to start within 4 weeks' },
        { id: 'quarter', title: 'ðŸ“† Within 3 months', description: 'Planning for next quarter' },
        { id: 'planning', title: 'ðŸ¤” Still planning', description: 'Gathering information for now' }
      ]
    },
    7: {
      message: "Great! Let me get some details about your business profile:",
      type: 'profile_form'
    },
    8: {
      message: "Based on your selections, here's what we recommend for your business setup:",
      type: 'recommendation'
    },
    9: {
      message: "Perfect! Let me collect your contact information to create your personalized report:",
      type: 'contact_form'
    },
    10: {
      message: "Excellent! Here's a summary of everything you've selected:",
      type: 'summary'
    },
    11: {
      message: "ðŸŽ‰ Thank you! Your business setup consultation has been scheduled. What would you like to do next?",
      options: [
        { id: 'calendar', title: 'ðŸ“… Book Calendar Appointment', description: 'Schedule a detailed consultation' },
        { id: 'contact', title: 'ðŸ“ž Get Contact Information', description: 'Speak with our specialists directly' },
        { id: 'restart', title: 'ðŸ”„ Start Over', description: 'Begin a new consultation' }
      ]
    }
  };

  // Validation functions
  const validateEmail = (email) => {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const validateLinkedIn = (url) => {
    if (!url) return true;
    const linkedinRegex = /^https:\/\/(www\.)?linkedin\.com\/in\/[a-zA-Z0-9-]+\/?$/;
    return linkedinRegex.test(url);
  };

  // Improved scroll to bottom function
  const scrollToBottom = () => {
    if (messagesEndRef.current) {
      messagesEndRef.current.scrollIntoView({ behavior: 'smooth', block: 'end' });
    }

    if (chatMessagesRef.current) {
      chatMessagesRef.current.scrollTop = chatMessagesRef.current.scrollHeight;
    }
  };

  // Auto-scroll to bottom with delay
  useEffect(() => {
    const timer = setTimeout(() => {
      scrollToBottom();
    }, 100);

    return () => clearTimeout(timer);
  }, [messages]);

  // Initialize chat
  useEffect(() => {
    if (messages.length === 0) {
      addMessage('bot', chatFlow[0].message);
    }
  }, []);

  const addMessage = (sender, text, timestamp = new Date().toLocaleTimeString()) => {
    setMessages(prev => [...prev, { sender, text, timestamp }]);
  };

  const handleOptionSelect = (optionId) => {
    const currentOptions = chatFlow[currentStep]?.options || [];
    const selectedOption = currentOptions.find(opt => opt.id === optionId);

    if (selectedOption) {
      addMessage('user', selectedOption.title);

      setSelectedOptions(prev => ({
        ...prev,
        [currentStep]: selectedOption
      }));

      if (selectedOption.price) {
        setTotalPrice(prev => prev + selectedOption.price);
      }

      setTimeout(() => {
        const nextStep = currentStep + 1;
        if (chatFlow[nextStep]) {
          setCurrentStep(nextStep);
          if (chatFlow[nextStep].message) {
            addMessage('bot', chatFlow[nextStep].message);
          }
        }
      }, 500);
    }
  };

  const handleFreeTextInput = async (text) => {
    addMessage('user', text);

    setTimeout(() => {
      const responses = [
        "Thank you for your question! Based on House of Companies' expertise, we specialize in helping businesses establish and expand across Europe and globally.",
        "That's a great question! Our team at House of Companies has extensive experience in international business formation and can provide detailed guidance on that topic.",
        "I understand your concern. House of Companies offers comprehensive support for business setup, tax compliance, and expansion strategies. Would you like to schedule a consultation to discuss this in detail?"
      ];
      const randomResponse = responses[Math.floor(Math.random() * responses.length)];
      addMessage('bot', randomResponse);
    }, 1000);

    setFreeTextInput('');
    setShowFreeTextInput(false);
  };

  const handleProfileSubmit = (profileData) => {
    setUserProfile(prev => ({ ...prev, ...profileData }));
    const nextStep = currentStep + 1;
    setCurrentStep(nextStep);
    if (chatFlow[nextStep]?.message) {
      addMessage('bot', chatFlow[nextStep].message);
    }
  };

  const handleContactSubmit = (contactData) => {
    const newErrors = {};

    if (!validateEmail(contactData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    if (contactData.linkedin && !validateLinkedIn(contactData.linkedin)) {
      newErrors.linkedin = 'Please enter a valid LinkedIn URL (https://linkedin.com/in/username)';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    setErrors({});
    setUserProfile(prev => ({ ...prev, ...contactData }));

    const nextStep = currentStep + 1;
    setCurrentStep(nextStep);
    if (chatFlow[nextStep]?.message) {
      addMessage('bot', chatFlow[nextStep].message);
    }
  };

  const generateReport = () => {
    const report = {
      sessionId,
      timestamp: new Date().toISOString(),
      userProfile,
      selectedServices: Object.values(selectedOptions),
      totalPrice,
      summary: Object.values(selectedOptions).map(opt =>
        `${opt.title}${opt.price ? ` - â‚¬${opt.price}` : ''}`
      ).join('\n')
    };
    return report;
  };

  const sendEmailReport = async (report) => {
    console.log('Sending report to manojnamalla12@gmail.com:', report);
    return new Promise(resolve => setTimeout(resolve, 2000));
  };

  const handleSubmit = async () => {
    setIsSubmitting(true);

    try {
      const report = generateReport();
      await sendEmailReport(report);

      addMessage('bot', 'âœ… Perfect! Your report has been sent to our team. You should receive a confirmation email shortly.');

      const nextStep = currentStep + 1;
      setCurrentStep(nextStep);
      if (chatFlow[nextStep]?.message) {
        setTimeout(() => {
          addMessage('bot', chatFlow[nextStep].message);
        }, 1000);
      }
    } catch (error) {
      addMessage('bot', 'âŒ Sorry, there was an issue sending your report. Please try again or contact us directly.');
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleBookConsultation = () => {
    const calendlyUrl = `https://calendly.com/dennis-houseofcompanies/new-meeting?month=2025-06&name=${encodeURIComponent(userProfile.name)}&email=${encodeURIComponent(userProfile.email)}`;
    window.open(calendlyUrl, '_blank');
    addMessage('bot', 'ðŸ“… Great! The calendar booking page has opened in a new window. Please select your preferred time slot.');
  };

  const goBack = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
      addMessage('bot', chatFlow[currentStep - 1].message);
    }
  };

  const startOver = () => {
    setCurrentStep(0);
    setMessages([]);
    setSelectedOptions({});
    setTotalPrice(0);
    setUserProfile({
      name: '',
      email: '',
      phone: '',
      linkedin: '',
      company: '',
      country: ''
    });
    setErrors({});
    setTimeout(() => {
      addMessage('bot', chatFlow[0].message);
    }, 100);
  };

  // Custom UI rendering
  const renderCurrentStep = () => {
    const step = chatFlow[currentStep];

    if (!step) return null;

    if (step.type === 'profile_form') {
      return (
        <div className="space-y-4">
          <div className="form-group">
            <label className="form-label">Company Name</label>
            <input
              type="text"
              className="form-input"
              value={userProfile.company}
              onChange={(e) => setUserProfile(prev => ({ ...prev, company: e.target.value }))}
              placeholder="Enter your company name"
            />
          </div>
          <div className="form-group">
            <label className="form-label">Preferred Country</label>
            <select
              className="form-select"
              value={userProfile.country}
              onChange={(e) => setUserProfile(prev => ({ ...prev, country: e.target.value }))}
            >
              <option value="">Select a country</option>
              {countries.map((country, index) => (
                <option key={index} value={country}>{country}</option>
              ))}
            </select>
          </div>
          <button
            onClick={() => handleProfileSubmit(userProfile)}
            className="btn btn-primary btn-full"
          >
            Continue
          </button>
        </div>
      );
    }

    if (step.type === 'contact_form') {
      return (
        <div className="space-y-4">
          <div className="form-group">
            <label className="form-label">Full Name *</label>
            <input
              type="text"
              className={`form-input ${errors.name ? 'error' : ''}`}
              value={userProfile.name}
              onChange={(e) => setUserProfile(prev => ({ ...prev, name: e.target.value }))}
              placeholder="Enter your full name"
              required
            />
          </div>
          <div className="form-group">
            <label className="form-label">Email Address *</label>
            <input
              type="email"
              className={`form-input ${errors.email ? 'error' : ''}`}
              value={userProfile.email}
              onChange={(e) => setUserProfile(prev => ({ ...prev, email: e.target.value }))}
              placeholder="Enter your email address"
              required
            />
            {errors.email && <p className="form-error">{errors.email}</p>}
          </div>
          <div className="form-group">
            <label className="form-label">Phone Number</label>
            <input
              type="tel"
              className="form-input"
              value={userProfile.phone}
              onChange={(e) => setUserProfile(prev => ({ ...prev, phone: e.target.value }))}
              placeholder="Enter your phone number"
            />
          </div>
          <div className="form-group">
            <label className="form-label">LinkedIn Profile</label>
            <input
              type="url"
              className={`form-input ${errors.linkedin ? 'error' : ''}`}
              value={userProfile.linkedin}
              onChange={(e) => setUserProfile(prev => ({ ...prev, linkedin: e.target.value }))}
              placeholder="https://linkedin.com/in/username"
            />
            {errors.linkedin && <p className="form-error">{errors.linkedin}</p>}
          </div>
          <button
            onClick={() => handleContactSubmit(userProfile)}
            className="btn btn-primary btn-full"
          >
            Continue
          </button>
        </div>
      );
    }

    if (step.type === 'recommendation') {
      return (
        <div className="space-y-4">
          <div className="recommendation-box">
            <h3 className="recommendation-title">ðŸŽ¯ Recommended for You</h3>
            <div className="space-y-2">
              {Object.values(selectedOptions).map((option, index) => (
                option.price > 0 && (
                  <div key={index} className="recommendation-item">
                    <span>{option.title}</span>
                    <span style={{fontWeight: 'bold'}}>â‚¬{option.price}</span>
                  </div>
                )
              ))}
            </div>
            <div style={{borderTop: '1px solid #d1fae5', marginTop: '12px', paddingTop: '12px'}}>
              <div className="recommendation-item">
                <span style={{fontWeight: 'bold'}}>Total Investment:</span>
                <span style={{fontSize: '1.25rem', fontWeight: 'bold'}}>â‚¬{totalPrice.toLocaleString()}</span>
              </div>
            </div>
          </div>
          <button
            onClick={() => {
              const nextStep = currentStep + 1;
              setCurrentStep(nextStep);
              if (chatFlow[nextStep]?.message) {
                addMessage('bot', chatFlow[nextStep].message);
              }
            }}
            className="btn btn-primary btn-full"
          >
            Looks Good! Continue
          </button>
        </div>
      );
    }

    if (step.type === 'summary') {
      return (
        <div className="space-y-4">
          <div className="summary-box">
            <h3 className="summary-title">ðŸ“‹ Your Business Setup Summary</h3>
            <div className="space-y-2">
              <div className="summary-item">
                <strong>Name:</strong> <span>{userProfile.name}</span>
              </div>
              <div className="summary-item">
                <strong>Email:</strong> <span>{userProfile.email}</span>
              </div>
              <div className="summary-item">
                <strong>Company:</strong> <span>{userProfile.company}</span>
              </div>
              <div className="summary-item">
                <strong>Country:</strong> <span>{userProfile.country}</span>
              </div>
              <div style={{paddingTop: '8px', borderTop: '1px solid #e5e7eb'}}>
                <strong>Selected Services:</strong>
                <div style={{marginTop: '8px'}}>
                  {Object.values(selectedOptions).map((option, index) => (
                    option.price > 0 && (
                      <div key={index} className="summary-item">
                        <span>{option.title}</span>
                        <span>â‚¬{option.price}</span>
                      </div>
                    )
                  ))}
                </div>
              </div>
              <div style={{paddingTop: '8px', borderTop: '1px solid #e5e7eb', fontWeight: 'bold'}}>
                <div className="summary-item">
                  <span>Total:</span>
                  <span>â‚¬{totalPrice.toLocaleString()}</span>
                </div>
              </div>
            </div>
          </div>
          <button
            onClick={handleSubmit}
            disabled={isSubmitting || !userProfile.email || !userProfile.name}
            className="btn btn-primary btn-full"
            style={{padding: '12px 24px', fontSize: '1rem'}}
          >
            {isSubmitting ? 'Processing...' : 'Complete & Send Report'}
          </button>
          <button
            onClick={handleBookConsultation}
            className="btn btn-primary btn-full"
            style={{padding: '12px 24px', fontSize: '1rem'}}
          >
            ðŸ“… Book Free Consultation
          </button>
        </div>
      );
    }

    // Horizontal buttons for options
    if (step.options) {
      return (
        <div>
          <div className="welcome-message">{step.message}</div>
          <div className="option-grid">
            {step.options.map((option, index) => (
              <button
                key={index}
                onClick={() => handleOptionSelect(option.id)}
                className="option-button-horizontal"
              >
                <div className="option-icon">{option.title.split(' ')[0]}</div>
                <div>
                  <div className="option-title">
                    {option.title.replace(/^\S+\s/, '')}
                  </div>
                  <div className="option-desc">{option.description}</div>
                  {option.price && option.price > 0 && (
                    <div className="option-price">â‚¬{option.price}</div>
                  )}
                </div>
              </button>
            ))}
          </div>
        </div>
      );
    }

    return null;
  };

  return (
    <div className="chatbot-container">
      {/* Header */}
      <div className="chatbot-header">
        <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center'}}>
          <div>
            <h1>House of Companies</h1>
            <p>Business Formation Assistant</p>
          </div>
          <div style={{textAlign: 'right', fontSize: '0.75rem'}}>
            <div>Session: {sessionId.split('-')[1]}</div>
            <div>{new Date().toLocaleDateString()}</div>
          </div>
        </div>
      </div>

      {/* Progress Bar */}
      <div className="chatbot-progress">
        <div className="progress-bar">
          <div
            className="progress-fill"
            style={{ width: `${Math.min((currentStep / totalSteps) * 100, 100)}%` }}
          ></div>
        </div>
        <div style={{display: 'flex', justifyContent: 'space-between', fontSize: '0.75rem', color: '#6b7280', marginTop: '4px'}}>
          <span>Progress</span>
          <span>{Math.min(Math.round((currentStep / totalSteps) * 100), 100)}%</span>
        </div>
      </div>

      {/* Chat Messages */}
      <div 
        ref={chatMessagesRef}
        className="chat-messages"
      >
        {messages.map((message, index) => (
          <div key={index} className={`message ${message.sender}`}>
            <div className="message-content">
              <div>{message.text}</div>
              <div className="message-time">{message.timestamp}</div>
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      {/* Current Step Content */}
      <div className="current-step-content">
        {renderCurrentStep()}
      </div>

      {/* Free Text Input */}
      {showFreeTextInput && (
        <div style={{padding: '16px', borderTop: '1px solid #e5e7eb', background: '#f8fafb'}}>
          <div className="free-text-input-container">
            <input
              type="text"
              value={freeTextInput}
              onChange={(e) => setFreeTextInput(e.target.value)}
              placeholder="Type your question..."
              className="form-input free-text-input"
              onKeyPress={(e) => {
                if (e.key === 'Enter' && freeTextInput.trim()) {
                  handleFreeTextInput(freeTextInput.trim());
                }
              }}
            />
            <button
              onClick={() => freeTextInput.trim() && handleFreeTextInput(freeTextInput.trim())}
              className="send-button"
            >
              Send
            </button>
          </div>
        </div>
      )}

      {/* Navigation & Action Buttons */}
      <div className="chatbot-footer">
        <div className="nav-buttons">
          {currentStep > 0 && (
            <button
              onClick={goBack}
              className="btn btn-secondary"
            >
              â† Back
            </button>
          )}
          <button
            onClick={startOver}
            className="btn btn-primary"
          >
            Start Over
          </button>
        </div>

        {/* Current Total Display */}
        {totalPrice > 0 && (
          <div className="total-display">
            <span className="label">Current Total:</span>
            <span className="amount">â‚¬{totalPrice.toLocaleString()}</span>
          </div>
        )}

        {/* Question Button */}
        <div className="mt-3">
          <button
            onClick={() => setShowFreeTextInput(!showFreeTextInput)}
            className="btn btn-outline btn-full"
          >
            ðŸ’¬ Ask a Question
          </button>
        </div>
      </div>
    </div>
  );
};

export default HouseOfCompaniesChatbot;
